INSERT INTO `wp_postmeta` VALUES (467,88,'_elementor_data','[{\"id\":\"88ba66d\",\"elType\":\"section\",\"settings\":{\"structure\":\"30\",\"background_background\":\"gradient\",\"background_overlay_background\":\"gradient\",\"padding\":{\"unit\":\"px\",\"top\":\"0\",\"right\":\"0\",\"bottom\":\"0\",\"left\":\"0\",\"isLinked\":true}},\"elements\":[{\"id\":\"30cfb33\",\"elType\":\"column\",\"settings\":{\"_column_size\":33,\"_inline_size\":10.523999999999999},\"elements\":[{\"id\":\"74512dc\",\"elType\":\"widget\",\"settings\":{\"editor\":\"<h2><b>History<\\/b><\\/h2>\"},\"elements\":[],\"widgetType\":\"text-editor\"}],\"isInner\":false},{\"id\":\"4a93b9e\",\"elType\":\"column\",\"settings\":{\"_column_size\":33,\"_inline_size\":14.404},\"elements\":[{\"id\":\"56f0ed6\",\"elType\":\"widget\",\"settings\":{\"editor\":\"<h2><a href=\\\"http:\\/\\/paozupperroom.local\\/six_pillars\\/\\\"><span style=\\\"text-decoration: underline;\\\"><b>Six Pillars<\\/b><\\/span><\\/a><\\/h2>\",\"text_color\":\"#7A7A7A\",\"typography_typography\":\"custom\",\"typography_font_family\":\"Roboto\",\"typography_font_weight\":\"400\",\"text_shadow_text_shadow_type\":\"yes\",\"text_shadow_text_shadow\":{\"horizontal\":0,\"vertical\":0,\"blur\":10,\"color\":\"\"},\"_background_background\":\"classic\",\"_background_image\":{\"url\":\"http:\\/\\/paozupperroom.local\\/wp-content\\/uploads\\/2021\\/10\\/cropped-PAOZ-LOGO.png\",\"id\":233,\"alt\":\"\",\"source\":\"library\"}},\"elements\":[],\"widgetType\":\"text-editor\"}],\"isInner\":false},{\"id\":\"97d4a25\",\"elType\":\"column\",\"settings\":{\"_column_size\":33,\"_inline_size\":74.736000000000004},\"elements\":[],\"isInner\":false}],\"isInner\":false},{\"id\":\"8b7b9d4\",\"elType\":\"section\",\"settings\":{\"background_background\":\"gradient\",\"background_image\":{\"url\":\"http:\\/\\/paozupperroom.local\\/wp-content\\/uploads\\/2021\\/10\\/cropped-PAOZ-LOGO.png\",\"id\":233,\"alt\":\"\",\"source\":\"library\"},\"background_size\":\"contain\",\"background_bg_width\":{\"unit\":\"%\",\"size\":95,\"sizes\":[]},\"background_hover_background\":\"classic\",\"background_hover_transition\":{\"unit\":\"px\",\"size\":0.20000000000000001,\"sizes\":[]},\"padding\":{\"unit\":\"px\",\"top\":\"0\",\"right\":\"0\",\"bottom\":\"0\",\"left\":\"0\",\"isLinked\":true}},\"elements\":[{\"id\":\"1a0dc76\",\"elType\":\"column\",\"settings\":{\"_column_size\":100,\"_inline_size\":null},\"elements\":[{\"id\":\"0064e52\",\"elType\":\"widget\",\"settings\":{\"editor\":\"<p class=\\\"MsoNormal\\\"><span style=\\\"color: #000000;\\\">PAOZ Upper Room Ministries is bible based Pentecostal church passionately fulfilling the Great commission and engaged in Social transformation. We are part of a network of local churches in the Pentecostal Assemblies of Zimbabwe throughout the nation Zimbabwe. The PAOZ was pioneered by missionaries from the Pentecostal Assemblies of Canada (PAOC) in 1948. Upper Room Ministries began in 1985 at Pentecost House.<\\/span><\\/p>\",\"align\":\"center\",\"typography_typography\":\"custom\",\"typography_font_family\":\"Roboto\",\"typography_font_weight\":\"400\"},\"elements\":[],\"widgetType\":\"text-editor\"},{\"id\":\"4721fd0\",\"elType\":\"widget\",\"settings\":{\"editor\":\"<h4 style=\\\"font-size: 19px; font-style: normal; font-weight: 600; margin-bottom: 0px; color: #161616; text-align: start;\\\" data-elementor-setting-key=\\\"title\\\" data-pen-placeholder=\\\"Type Here...\\\"><span style=\\\"color: #000000; font-size: 1.25rem;\\\">Vision<\\/span><\\/h4><h3 style=\\\"text-align: left;\\\"><span style=\\\"font-size: 20px; font-style: normal; font-weight: 400; color: #000000; text-align: center;\\\">House To Know Christ,Make Him known and Change the world.<\\/span><\\/h3>\",\"align\":\"center\",\"typography_typography\":\"custom\",\"typography_font_family\":\"Roboto\",\"typography_font_weight\":\"400\"},\"elements\":[],\"widgetType\":\"text-editor\"},{\"id\":\"450df61\",\"elType\":\"widget\",\"settings\":{\"editor\":\"<h4 style=\\\"font-size: 19px; font-style: normal; font-weight: 600; margin-bottom: 0px; color: #161616; text-align: left;\\\" data-elementor-setting-key=\\\"title\\\" data-pen-placeholder=\\\"Type Here...\\\"><span style=\\\"font-size: 1.25rem; color: #000000;\\\">Mission<\\/span><\\/h4><div><h3 style=\\\"font-style: normal; text-align: left;\\\"><span style=\\\"color: #000000; font-size: 20px; font-style: normal; font-weight: 400; text-align: center; font-family: var( --e-global-typography-text-font-family ), Sans-serif;\\\">We are a Bible based Pentecostal Church passionate about fulfilling the Great Commission and engaged in Social\\u00a0 Transformation.<\\/span><\\/h3><\\/div>\",\"align\":\"center\"},\"elements\":[],\"widgetType\":\"text-editor\"},{\"id\":\"3f1d888\",\"elType\":\"widget\",\"settings\":{\"editor\":\"<p class=\\\"MsoNormal\\\" style=\\\"margin-bottom: 0.0001pt; line-height: normal; background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial;\\\"><span style=\\\"color: #000000;\\\"><b><span style=\\\"font-size: 14.5pt; font-family: Arial, sans-serif; padding: 0in; border: 1pt none windowtext;\\\">People<\\/span><\\/b><b><\\/b><\\/span><\\/p><p class=\\\"MsoNormal\\\"><span style=\\\"color: #000000;\\\">We are committed to loving people and sharing the gospel of Jesus Christ at every opportunity.(John 3:16)<\\/span><\\/p><p class=\\\"MsoNormal\\\" style=\\\"margin-bottom: 0.0001pt; line-height: normal; background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial;\\\"><span style=\\\"color: #000000;\\\"><b><span style=\\\"font-size: 14.5pt; font-family: Arial, sans-serif; padding: 0in; border: 1pt none windowtext;\\\">Fellowship<\\/span><\\/b><b><\\/b><\\/span><\\/p><p class=\\\"MsoNormal\\\"><span style=\\\"color: #000000;\\\">We are committed to creating an environment where believers can relate to one another with genuine love and acceptance (Ephesians 2:20-22; Romans 12:4-5)<\\/span><\\/p><p class=\\\"MsoNormal\\\" style=\\\"margin-bottom: 0.0001pt; line-height: normal; background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial;\\\"><span style=\\\"color: #000000;\\\"><b><span style=\\\"font-size: 14.5pt; font-family: Arial, sans-serif; padding: 0in; border: 1pt none windowtext;\\\">Discipleship<\\/span><\\/b><b><\\/b><\\/span><\\/p><p class=\\\"MsoNormal\\\"><span style=\\\"color: #000000;\\\">We are committed to creating an environment where believers can relate to one another with genuine love and acceptance (Ephesians 2:20-22; Romans 12:4-5)<\\/span><\\/p><p class=\\\"MsoNormal\\\" style=\\\"margin-bottom: 0.0001pt; line-height: normal; background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial;\\\"><span style=\\\"color: #000000;\\\"><b><span style=\\\"font-size: 14.5pt; font-family: Arial, sans-serif; padding: 0in; border: 1pt none windowtext;\\\">Pentecostal Heritage<\\/span><\\/b><b><\\/b><\\/span><\\/p><p class=\\\"MsoNormal\\\"><span style=\\\"color: #000000;\\\">We are committed to seeing the person and the power of the Holy Spirit in all we do. (Mark 15:15-17; Acts 1:8; Acts 2:4)<\\/span><\\/p><p class=\\\"MsoNormal\\\" style=\\\"margin-bottom: 0.0001pt; line-height: normal; background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial;\\\"><span style=\\\"color: #000000;\\\"><b><span style=\\\"font-size: 14.5pt; font-family: Arial, sans-serif;\\\">Sound Doctrine<\\/span><\\/b><\\/span><\\/p><p class=\\\"MsoNormal\\\"><span style=\\\"color: #000000;\\\">We are committed to proclaiming and practicing the whole counsel of God as found in the Holy Bible wit integrity and accuracy (Luke 24:19; Acts 1:1; Timothy 3:16; Acts 20:20)<\\/span><\\/p><p class=\\\"MsoNormal\\\" style=\\\"margin-bottom: 0.0001pt; line-height: normal; background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial;\\\"><span style=\\\"color: #000000;\\\"><b><span style=\\\"font-size: 14.5pt; font-family: Arial, sans-serif; padding: 0in; border: 1pt none windowtext;\\\">Excellence<\\/span><\\/b><b><\\/b><\\/span><\\/p><p class=\\\"MsoNormal\\\"><span style=\\\"color: #000000;\\\">We are committed to seeing excellence exhibited in all our endeavors upholding the beauty of God (2 Corinthians 8:7; Daniel 5:12; 1 Corinthians 14:40)<\\/span><\\/p><p class=\\\"MsoNormal\\\" style=\\\"margin-bottom: 0.0001pt; line-height: normal; background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial;\\\"><span style=\\\"color: #000000;\\\"><b><span style=\\\"font-size: 14.5pt; font-family: Arial, sans-serif; padding: 0in; border: 1pt none windowtext;\\\">Integrity<\\/span><\\/b><b><\\/b><\\/span><\\/p><p class=\\\"MsoNormal\\\"><span style=\\\"color: #000000;\\\">We are committed to transparency and honesty in all our personal, public and corporate affairs. (Daniel 6:3-4; Romans 14:16; 2 Thessalonians 5:21-22)<\\/span><\\/p><p class=\\\"MsoNormal\\\" style=\\\"margin-bottom: 0.0001pt; line-height: normal; background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial;\\\"><span style=\\\"color: #000000;\\\"><b><span style=\\\"font-size: 14.5pt; font-family: Arial, sans-serif; padding: 0in; border: 1pt none windowtext;\\\">Accountability<\\/span><\\/b><b><\\/b><\\/span><\\/p><p class=\\\"MsoNormal\\\"><span style=\\\"color: #000000;\\\">We are committed to building Structures and practices that will enhance accountability with the understanding that God shall require us to give an account (Romans 12:14)<\\/span><\\/p><p class=\\\"MsoNormal\\\" style=\\\"margin-bottom: 0.0001pt; line-height: normal; background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial;\\\"><span style=\\\"color: #000000;\\\"><span style=\\\"font-size: 14.5pt; font-family: Arial, sans-serif; padding: 0in; border: 1pt none windowtext;\\\"><b>Organizational Structure<br \\/><\\/b><\\/span><span style=\\\"font-size: 20px;\\\">The operation of the work and ministry of the PAOZ is carried out through its structures.<\\/span><\\/span><\\/p>\",\"align\":\"center\",\"typography_typography\":\"custom\",\"typography_font_family\":\"Roboto\",\"typography_font_weight\":\"400\"},\"elements\":[],\"widgetType\":\"text-editor\"}],\"isInner\":false}],\"isInner\":false}]');
